package com.yem.leetcode;import java.util.Arrays;/** * @author: * @date: 2024-05-21 * @describe: * 给你两个按 非递减顺序 排列的整数数组 nums1 和 nums2，另有两个整数 m 和 n ，分别表示 nums1 和 nums2 中的元素数目。 * 请你 合并 nums2 到 nums1 中，使合并后的数组同样按 非递减顺序 排列。 * @return:*/public class CombineArray {	public static void main(String[] args) {		int[] first = {2,0};		int[] secode = {1};		combineArray(first, 1, secode, 1);	}	private static void combineArray(int[] nums1, int m, int[] nums2, int n){		int [] first = Arrays.copyOf(nums1, nums1.length);		int fi = 0;		int si = 0;		int ri = 0;		while(fi < m && si < n){			int fe = first[fi];			int se = nums2[si];			if(fe < se){				nums1[ri] = fe;				fi++;				ri++;			} else if (fe == se) {				nums1[ri] = fe;				fi++;				ri++;				nums1[ri] = se;				si++;				ri++;			} else if (fe > se) {				nums1[ri] = se;				si++;				ri++;			}		}		if(fi == m){			while (si < n){				nums1[ri] = nums2[si];                si++;                ri++;			}		} else if (si ==n) {			while (fi < m){				nums1[ri] = first[fi];                fi++;                ri++;			}		}		System.out.println(nums1);	}}