package com.yem.leetcode;import java.util.HashMap;import java.util.Map;public class ConvertRomeNum {	private static Map<Character, Integer> map = new HashMap<Character, Integer>();	static {		map.put('I', 1);		map.put('V', 5);		map.put('X', 10);		map.put('L', 50);		map.put('C', 100);		map.put('D', 500);		map.put('M', 1000);	}	public static void main(String[] args) {		System.out.println(convert("MCMXCIV"));	}	public static int convert(String s){		int result = 0;		char[] chars = s.toCharArray();		for(int i = 0; i < chars.length; i++){			Integer num = map.get(chars[i]);			if(i < chars.length -1 && num < map.get(chars[i + 1])){				result += map.get(chars[i + 1]) - num;				i++;			}else {				result += num;			}		}		return result;	}}