package com.yem.leetcode;public class GasAndCost {	public static void main(String[] args) {		int[] gas = {1,2,3,4,5};		int[] cost = {3,4,5,1,2};		addGas(gas, cost);	}	/**	 * 假如从x开始不能达到y处，则x和y之间的点都不能达到y处，	 * 下一次循环从y直接开始	 *	 */	public static int addGas(int[] gas, int[] cost){		int starter = 0;		while(starter < gas.length){			int totalGas = 0;			int totalCost = 0;			int cnt = 0;			while (cnt < cost.length){				int index = (cnt + starter) % cost.length;				totalGas += gas[index];				totalCost += cost[index];				if(totalGas < totalCost){					break;				}				cnt++;			}			if(cnt == gas.length){				return starter;			}else {				starter = starter + cnt + 1;			}		}		return 0;	}}